CREATE TABLE `ts_superordinate_courses` ( `id` INT NOT NULL AUTO_INCREMENT , `changed` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `created` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `creator_id` INT NULL DEFAULT NULL , `editor_id` INT NULL DEFAULT NULL , `active` TINYINT NOT NULL DEFAULT '1' , PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `ts_superordinate_courses_i18n` ( `superordinate_course_id` INT NOT NULL , `language_iso` VARCHAR(50) NOT NULL , `name` VARCHAR(1000) NOT NULL , INDEX (`superordinate_course_id`)) ENGINE = InnoDB;
ALTER TABLE `kolumbus_tuition_courses` ADD `superordinate_course_id` INT NOT NULL AFTER `category_id`, ADD INDEX (`superordinate_course_id`);
