CREATE TABLE IF NOT EXISTS `notices` (`id` int(11) NOT NULL AUTO_INCREMENT, `created` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, `creator_id` int(11) NOT NULL,  `active` tinyint(4) NOT NULL DEFAULT '1',  `entity` varchar(255) NOT NULL,  `entity_id` int(11) NOT NULL,  `notice` text NOT NULL,  PRIMARY KEY (`id`),  KEY `active` (`active`),  KEY `entity` (`entity`(191),`entity_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;